import { useState, useRef, useEffect } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import QuotationTable from "./QuotationTable";
import { Container, Row, Col, Form, Button } from "react-bootstrap";
import useLocalStorage from "react-localstorage-hook";
import dataCS from "./cs-2019.json"
import React from 'react';
import Select from 'react-select';
import Table2 from "./table2";


function App() {

  const subjectNRef = useRef();
  const gradeRef = useRef();
  const semRef = useRef();
  const codeRef = useRef();

  // const [oneonenine, setoneonenine] = useState([])
  // var twoonenine = []
  // var onetwozero = []
  // var twotwozero = []
  // var onetwoone = []
  // var twotwoone = []

  const [dataItems, setDataItems] = useLocalStorage("dataItems", []);
  const [dataItems2, setDataItems2] = useLocalStorage("dataItems2", []);


  const gradeList = [
    { id: "01", grade: "A", num: 4.00 },
    { id: "02", grade: "A-", num: 3.75 },
    { id: "03", grade: "B+", num: 3.25 },
    { id: "04", grade: "B", num: 3.00 },
    { id: "05", grade: "B-", num: 2.75 },
    { id: "06", grade: "C+", num: 2.25 },
    { id: "07", grade: "C", num: 2.00 },
    { id: "08", grade: "C-", num: 1.75 },
    { id: "09", grade: "D", num: 1.00 },
    { id: "10", grade: "F", num: 0 },
    { id: "11", grade: "W,I,S,U,R,TR ", num: 0 }
  ];

  const semesters = [
    { id: "01", term: "1/2019" },
    { id: "02", term: "2/2019" },
    { id: "03", term: "1/2020" },
    { id: "04", term: "2/2020" },
    { id: "05", term: "1/2021" },
    { id: "06", term: "2/2021" }
  ]



  const addItem = () => {
    if (codeRef.current.value == "" || semRef.current.value == "" || gradeRef.current.value == "" || subjectNRef.current.value == "") {
      alert("Required Input is empty");
      return;
    }
    const grd = gradeRef.current.value
    const grade = gradeList.find(e => e.id === grd)
    const semes = semRef.current.value
    const sem = semesters.find(e => e.id === semes)

    var itemObj = {

      sem: sem.term,
      code: codeRef.current.value,
      subN: subjectNRef.current.value,
      grd: grade.grade,
      grdn: grade.num

    };

    // dataItems.push(itemObj);
    // setDataItems([...dataItems]);


    if (semRef.current.value == 1/2019) {
      dataItems.push(itemObj);
      setDataItems([...dataItems]);
    } else {
      dataItems2.push(itemObj);
      setDataItems2([...dataItems2]);
    }

    // const found = dataItems.some(e => e.sem === itemObj.sem)
    // if (!found) {
    //   dataItems.push(itemObj);
    //   setDataItems([...dataItems]);
    // } else {
    //   var index = dataItems.findIndex((obj => obj.sem == itemObj.sem));
    //   dataItems[index].push(itemObj);
    //   setDataItems([...dataItems]);
    // }


    // if (parseInt(disRef.current.value, 10) > (parseInt(ppuRef.current.value, 10) * parseInt(qtyRef.current.value, 10))) {
    //   alert("The discount is more than the total price, please re-enter ")
    //   return;
    // } else {
    // const found = dataItems.some(e => e.pid === itemObj.pid);
    //   if (!found) {
    //     dataItems.push(itemObj);
    //     setDataItems([...dataItems]);


    //   } else {
    //     var index = dataItems.findIndex((obj => obj.pid == itemObj.pid));
    //     dataItems[index].qty = parseInt(dataItems[index].qty, 10) + parseInt(qtyRef.current.value, 10);
    //     dataItems[index].dis = parseInt(dataItems[index].dis, 10) + parseInt(disRef.current.value, 10);
    //     setDataItems([...dataItems]);

    //   }

    // }
  };

  const subjectChange = (e) => {
    const subcode = codeRef.current.value;
    const subs = dataCS.curriculum.subjects.map((item) => {
      return item.subjects.findIndex((e) => e.code === subcode) //return array 0,-1,-1,-1,-1,-1,-1,-1
    });

    const subcodearr = (item) => item > -1;
    const sub1arr = subs.findIndex(subcodearr)

    const i = dataCS.curriculum.subjects[sub1arr].subjects
    const r = dataCS.curriculum.subjects[sub1arr].subjects.findIndex((item) => {
      return item.code === subcode}) 

    const getname = dataCS.curriculum.subjects[sub1arr].subjects[r].name

    subjectNRef.current.value = getname
    //subjectNRef.current.value = semRef.current.value


  }

  const gradeOptions = gradeList.map(v => {
    return <option value={v.id} key={v.id}>{v.grade}</option>
  })

  const semesOptions = semesters.map(v => {
    return <option value={v.term} key={v.id}>{v.term}</option>
  })

  const subjectOption = dataCS.curriculum.subjects.map((item) => {
    return <option>{item.groupName}</option>
  })

  //code in sub-subjects
  const subjectOptionA = dataCS.curriculum.subjects.map((item) => {
    return item.subjects.map((e) => {
      return <option>{e.code}</option>
    })
  })

  const subjectOptionB = dataCS.curriculum.subjects.map((item) => {
    return item.subjects.map((e) => {
      return <option>{e.name}</option>
    })
  })


  return (
    <Container>
      <Row>
        <Col xs={5} style={{ backgroundColor: "#eaeaea" }}>
          <Form>

            <Form.Group classgrade="mb-3" controlId="formsemester">
              <Form.Label>Semester</Form.Label>
              <Form.Select aria-label="Default select example" ref={semRef}  >
                {semesOptions}
              </Form.Select>
            </Form.Group>

            <Form.Group classgrade="mb-3" controlId="formItem">
              <Form.Label>Subject code</Form.Label>
              <Form.Select aria-label="Default select example" ref={codeRef} onChange={subjectChange}>

                {subjectOptionA}
              </Form.Select>
            </Form.Group>

            <Form.Group classgrade="mb-3" controlId="formPrice">
              <Form.Label>Subject name</Form.Label>
              <Form.Control type="string" placeholder="Subject Name" ref={subjectNRef} />
            </Form.Group>

            <Form.Group classgrade="mb-3" controlId="formDiscount">
              <Form.Label>Grade</Form.Label>
              <Form.Select aria-label="Default select example" ref={gradeRef} >

                {gradeOptions}
              </Form.Select>
            </Form.Group>

            {/* <Button variant="outline-dark" size="lg" onClick={addItem}> */}
            <div classgrade="d-grid gap-2">
              <Button variant="primary" size="lg" onClick={addItem}>
                Add
              </Button>
            </div>
          </Form>

        </Col>
        <Col>
          <QuotationTable data={dataItems} setDataItems={setDataItems} />
          <Table2 data={dataItems2} setDataItems={setDataItems2} />
        </Col>
        {/* <Col>
         <Table2 data={dataItems} setDataItems={setDataItems} />
         </Col> */}
      </Row>
    </Container>


  )
}

export default App;
