import { useState, useRef, useEffect } from "react";
import { Container, Row, Col, Table, Button } from "react-bootstrap";


const styles = {
  textCenter : {textAlign: 'center'},
  textRight : {textAlign: 'right'}
}

function QuotationTable({ data, setDataItems }) {
  const [dataRows, setDataRows] = useState();
  const [totalPrice, setTotalPrice] = useState(0);
  const [totalDiscount, setTotalDiscount] = useState(0);


  useEffect(() => {
    let sum = 0
    let sumD = 0
    const z = data.map((v, i) => {
      let amount = (v.qty * v.ppu) - v.dis; 
      sum += amount;
      sumD += parseInt(v.dis, 10)
      return (
        <tr key={i}>
          <td style={styles.textCenter}>{v.qty}</td>
          <td style={styles.textCenter}>{v}</td>
          <td style={styles.textRight}>{numberWithCommas(v.ppu)}</td>
          <td style={styles.textRight}>{numberWithCommas(v.dis)}</td>
          <td style={styles.textRight}>{numberWithCommas(amount)}</td>
        </tr>
      );
    });

    setDataRows(z);
    setTotalPrice(sum)
    setTotalDiscount(sumD, 10)
  }, [data]);

  const deleteClick = (i) => {
    data.splice(i,1)
    setDataItems([...data])
  }
  const numberWithCommas = (x) => {
    return x.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
  };

  const clearTable = () => {
    setDataItems([]);
    setDataRows([]);
  };

  return (
    <Container>
      <Row>
        <Col>
          <h1>Computer Science</h1>
        </Col>
        <Col style={styles.textRight}>
          <Button  onClick={clearTable} variant="dark">
            Clear
          </Button>
        </Col>
      </Row>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th></th>
            <th style={styles.textCenter}>Subject</th>
            <th style={styles.textCenter}>Grade</th>
            <th style={styles.textCenter}>Price/Unit</th>
            <th style={styles.textCenter}>Discount</th>
            <th style={styles.textCenter}>Grade</th>

          </tr>
        </thead>
        <tbody>{dataRows}</tbody>
        <tfoot>
        <tr>
          <th colSpan={3}></th>
          <th style={styles.textRight}>Total Discount/Amount</th>
          <th style={styles.textRight}>{numberWithCommas(totalDiscount)}</th>
          <th style={styles.textRight}>{numberWithCommas(totalPrice)}</th>
          </tr>
        </tfoot>
      </Table>
    </Container>
  );
}

export default QuotationTable;
